
public class 문제_05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10;   
	      System.out.printf("%d \n", num1); // 10
	      num1++;
	      System.out.printf("%d \n", num1); // 11
	      ++num1;
	      System.out.printf("%d \n", num1); // 12
	      --num1;
	      System.out.printf("%d \n", num1); // 11
	      num1--;
	      System.out.printf("%d \n", num1); // 10
	      
	      int a=10;
	      System.out.println(a++);
	      System.out.println(a++);
	}

}
